mapper.select:
select id, fa.city_code as cityCode, fa.subway_car_code as subwayCarCode, fa.manufacturer_code as manufacturerCode, fa.production_batch_code as productionBatchCode, fa.line_code as lineCode, fa.failure_cause as failureCause, fa.analysis_result as analysisResult, fa.happening_time as happeningTime, fa.create_user as createUser, fa.create_time as createTime from failure_analysis fa
AND CONCAT_WS(',' , city_code , subway_car_code , manufacturer_code , production_batch_code , line_code , failure_cause , analysis_result , happening_time ) Like #{advanceColumnLike}
limit #{_limit} offset #{_offset}
