mapper.select:
select id, deviceId, command, agreement, m_vstate as mVstate , m_astate as mAstate , m_tstate as mTstate , d_vstate as dVstate , d_astate as dAstate , d_tstate as dTstate , degree, created_at as createdAt from monitor_data
and deviceId = #{deviceId}
limit #{_limit} offset #{_offset}
order by created_at desc
