mapper.select:
select id, u.user_name as userName, display_name as displayName, password, last_login_time as lastLoginTime, u.create_user as createUser, u.create_time as createTime, update_user as updateUser, u.update_time as updateTime, sex,phone from user u
and ur.role_code = #{roleCode}
and u.user_name like #{name}
and u.password = #{passwordEqual}
limit #{_limit} offset #{_offset}


select count(*) from user u


    join cms_user_role ur on u.name = ur.user_name



        and ur.role_code = #{roleCode}


        and u.user_name like #{name}


        and u.password = #{passwordEqual}
